  _.each($scope.componentFilters, function (element, index) {
    componentLabel = element.charAt(0).toUpperCase() + element.substring(1).toLowerCase();
    $scope.componentFilters[index] = {
      'capitalized': componentLabel,
      'lowercase': element
    };
    componentLabel = null;
  });

