        var endDate = product.sale_date_to;
        if (!startDate && !endDate) {
          startDate = moment();
          endDate = moment().add(6, 'days');
        }
        $scope.selectedDate.range = {
          startDate: new Date(startDate),
          endDate: new Date(endDate)
        };
