      _.each(pages, function (page) {
        if (page) {
          page.hasScreenshot = false;
          if (page.screenshot) {
            if ($("#screenshot_" + page._id).attr("src") === $scope.default_image_url) {
              page.hasScreenshot = false;
            } else {
              page.hasScreenshot = true;
            }
          }
        }
      });
